    public static ArrayList<Team> getListData() {
        ArrayList<Team> list = new ArrayList<>();
        for (String[] aData : data) {
            Team club = new Team();
            club.setName(aData[0]);
            club.setKonten(aData[1]);
            club.setPic(aData[2]);
            list.add(club);
        }
        return list;
    }
